#ifndef _scc8660_h_
#define _scc8660_h_
#include "sensor.h"
// 定义凌瞳在复位时的延时时间，内部软延时（480M主频时 单位约为1ms）
#define SCC8660_RESET_DELAY 50

int SCC8660_init(sensor_t *sensor);
extern void scc8660_iic_write(unsigned char addr, unsigned char reg, unsigned char data);
extern void scc8660_iic_read(unsigned char addr, unsigned char reg, unsigned char *data);


//-------------------------------------------------------------------------------------------------------------------
// 函数简介     凌瞳ID获取
// 参数说明     void      
// 返回参数     int            8660：找到正确ID   
// 使用示例     
//-------------------------------------------------------------------------------------------------------------------
int scc8660_get_id(void);

//-------------------------------------------------------------------------------------------------------------------
// 函数简介     凌瞳帧率调节
// 参数说明     framerate      可设置参数2、15、30、50、60 如果填写其他值都会按照50帧进行设置
// 返回参数     int            0：成功  
// 使用示例     
//-------------------------------------------------------------------------------------------------------------------
int scc8660_set_framerate(int framerate);

//-------------------------------------------------------------------------------------------------------------------
// 函数简介     凌瞳分辨率调节
// 参数说明     framesize      1:640*480 2:320*240 3：240*160 4:160*120   其他：320*240
// 返回参数     int            0：成功  
// 使用示例     
//-------------------------------------------------------------------------------------------------------------------
int scc8660_set_framesize(int framesize);

//-------------------------------------------------------------------------------------------------------------------
// 函数简介     凌瞳曝光调节
// 参数说明     level          手动曝光是参数范围0-65535   自动曝光时参数设置范围0-255    
// 返回参数     int            0：成功  
// 使用示例     
//-------------------------------------------------------------------------------------------------------------------
int scc8660_set_brightness(int level);

//-------------------------------------------------------------------------------------------------------------------
// 函数简介     凌瞳自动曝光
// 参数说明     enable          0：手动曝光  1：自动曝光                 
// 返回参数     exposure_value  手动曝光是参数范围0-65535   自动曝光时参数设置范围0-255
// 返回参数     int             0：成功  
// 使用示例     
//-------------------------------------------------------------------------------------------------------------------
int scc8660_set_auto_exposure(int enable, int exposure_value);

//-------------------------------------------------------------------------------------------------------------------
// 函数简介     凌瞳自动白平衡调节
// 参数说明     enable          0：手动白平衡调节  1：自动白平衡调节
// 参数说明     r_gain_db       R分量增益调节（仅在手动白平衡时设置有效）
// 参数说明     g_gain_db       G分量增益调节（仅在手动白平衡时设置有效）
// 参数说明     b_gain_db       B分量增益调节（仅在手动白平衡时设置有效）
// 返回参数     int             0：成功  
// 使用示例     
//-------------------------------------------------------------------------------------------------------------------
int scc8660_set_auto_whitebal(int enable, int r_gain_db, int g_gain_db, int b_gain_db);

//-------------------------------------------------------------------------------------------------------------------
// 函数简介     凌瞳图像水平翻转
// 参数说明     enable          0：不翻转  1：翻转                 
// 返回参数     int             0：成功  
// 使用示例     
//-------------------------------------------------------------------------------------------------------------------
int scc8660_set_hmirror(int enable);

//-------------------------------------------------------------------------------------------------------------------
// 函数简介     凌瞳图像垂直翻转
// 参数说明     enable          0：不翻转  1：翻转                 
// 返回参数     int             0：成功  
// 使用示例     
//-------------------------------------------------------------------------------------------------------------------
int scc8660_set_vflip(int enable);

//-------------------------------------------------------------------------------------------------------------------
// 函数简介     凌瞳初始化函数
// 参数说明     void                 
// 返回参数     int             0：初始化成功   -1：初始化失败，可能是IIC通讯异常
// 使用示例     
// 注意事项                     初始化后，默认分辨率为320*240、50帧、手动曝光、RGB565格式（高八位先输出）     
//-------------------------------------------------------------------------------------------------------------------
int scc8660_init(void);


#endif


